// Mobile Menu Toggle
const menuToggleButton = document.querySelector('.menu-toggle');
const mobileNav = document.querySelector('.mobile-nav');

if (menuToggleButton && mobileNav) {
    menuToggleButton.addEventListener('click', () => {
        mobileNav.classList.toggle('active');
        // Optional: Change icon on toggle
        const icon = menuToggleButton.querySelector('ion-icon');
        if (mobileNav.classList.contains('active')) {
            icon.setAttribute('name', 'close-outline');
        } else {
            icon.setAttribute('name', 'menu-outline');
        }
    });
}

// Close mobile nav when a link is clicked
function closeMobileNav() {
    if (mobileNav.classList.contains('active')) {
        mobileNav.classList.remove('active');
        const icon = menuToggleButton.querySelector('ion-icon');
        if (icon) {
            icon.setAttribute('name', 'menu-outline');
        }
    }
}


// Modal logic
const modals = document.querySelectorAll('.modal');

function openModal(projectId) {
    const modal = document.getElementById(`modal-${projectId}`);
    if (modal) {
        modal.classList.add('active');
        document.body.style.overflow = 'hidden'; // Prevent scrolling background
    }
}

function closeModal() {
    modals.forEach(modal => modal.classList.remove('active'));
    document.body.style.overflow = ''; // Restore scrolling background
}

// Close modal when clicking outside the modal-box
modals.forEach(modal => {
    modal.addEventListener('click', (e) => {
        if (e.target === modal) { // Check if the click was directly on the overlay
            closeModal();
        }
    });
});

// Close modal when Escape key is pressed
document.addEventListener('keydown', function(e) {
    if (e.key === 'Escape') {
        closeModal();
    }
});

